import React, { useState, useEffect } from 'react';

// Componente principal da página de produtos
function product() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      // produtos estáticos para exemplo simples
      const productsData = [
        { id: 1, name: 'Produto A', price: 100, description: 'Descrição do Produto A' },
        { id: 2, name: 'Produto B', price: 150, description: 'Descrição do Produto B' },
      ];
      setProducts(productsData);
    };

    fetchProducts();
  }, []);

  const addToCart = (product) => {
    console.log(`Produto adicionado ao carrinho: ${product.name}`);
  };

  return (
    <div>
      <h1>Produtos Disponíveis</h1>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <p>Preço: R${product.price}</p>
            <button onClick={() => addToCart(product)}>Adicionar ao Carrinho</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default product;
