import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import './checkout.css';


function Checkout() {
  const location = useLocation();
  const [cart, setCart] = useState([]);
  const [isPaid, setIsPaid] = useState(false);

  // useEffect(() => {
  //   if (location.state && location.state.cart) {
  //     setCart(location.state.cart);
  //   }
  // }, [location.state]);
  useEffect(() => {
    const storedCart = localStorage.getItem('cart');
    if (storedCart) {
      setCart(JSON.parse(storedCart));
    }
  }, []);

  useEffect(() => {
    console.log('Carrinho de Compras no Checkout:', cart);
  }, [cart]);

  const handlePayment = () => {
    setIsPaid(true);
  };

  return (
    <div>
    <h1>Checkout</h1>
    <div className="cart-preview">
      <h2>Carrinho</h2>
      {itemCount === 0 ? (
        <p>Carrinho vazio</p>
      ) : (
        <>
          <ul>
            {cart.map((product, index) => (
              <li key={index}>
                {product.name} - R${product.price}
              </li>
            ))}
          </ul>
          <p>Total de itens: {itemCount}</p>
          <p>Total: R${totalAmount}</p>
        </>
      )}
    </div>
    {itemCount > 0 && !payment && (
      <button onClick={handlePayment}>Pagar</button>
    )}
    {payment && (
      <div>
        <p>Compra realizada com sucesso!</p>
        <p>Data da compra: {new Date().toLocaleString()}</p>
        <Link to="/">Voltar para Produtos</Link>
      </div>
    )}
  {!isPaid ? (
        <button onClick={handlePayment}>Pagar</button>
      ) : (
        <div>
          <p>Pagamento realizado com sucesso!</p>
          <Link to="/product">
            <button>Voltar aos Produtos</button>
          </Link>
          <Link to="/profile">
            <button>Ir para o Perfil</button>
          </Link>
        </div>
      )}
    </div>
  );
}

export default Checkout;
